sp_widget.client_script:
api.controller=function($rootScope, $scope) {
	/* widget controller */
	var c = this;
	$scope.updateNote = function(updateType) {
		this.server.get({
			action : 'updateNote',
			noteID : $scope.note.sys_id,
			noteTitle : $scope.note.title,
			noteBody : $scope.note.note
		}).then(function(r){
			$rootScope.updatedNote = $scope.note;
			$rootScope.$emit('updateNote', $scope.note)
		});
	};
	$rootScope.$on('selectNote', function(event,data) { 
		console.log('Listener caught NoteID: ' + $rootScope.noteID);
		c.server.get({
			action : 'getNoteDetails',
			noteID : $rootScope.noteID
		}).then(function(result) {
			$scope.note = result.data.note;
		});
	})
};

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	if(input && input.noteID) {
		var note = new GlideRecord('x_snc_createnotes_note');
		if(note.get(input.noteID)) {
			if(input.action == 'getNoteDetails') {
				data.note = {};
				$sp.getRecordValues(data.note, note, "title, note, sys_id"); 
			}
			else if(input.action == 'updateNote') {
				note.title = input.noteTitle;
				note.note = input.noteBody;
				note.update();
			}
		}
	}
})();
